  }

  # Initial parameters for tracking the longest increasing sequence
  max_length <- 1
  current_length <- 1

  for (i in 2:length(temperatures)) {
    if (temperatures[i] > temperatures[i - 1]) {
      current_length <- current_length + 1
      max_length <- max(max_length, current_length)
    } else {
      current_length <- 1
    }
  }

  return(max_length)
}
test_longest_increasing_temperatures <- function() {
    stopifnot(longest_increasing_temperatures(c(1, 2, 3, 4)) == 4)
    stopifnot(longest_increasing_temperatures(c(4, 3, 2, 1)) == 1)
    stopifnot(longest_increasing_temperatures(c(1, 2, 1, 3, 2, 4)) == 2)
    stopifnot(longest_increasing_temperatures(c(1, 1, 1, 1)) == 1)
    stopifnot(longest_increasing_temperatures(numeric(0)) == 0)
}

