max_crossing_sum <- function(arr, left, mid, right) {
    sum_left <- -Inf
    sum_temp <- 0

    for (i in mid:left) {
        sum_temp <- sum_temp + arr[i]
        if (sum_temp > sum_left) {
            sum_left <- sum_temp
        }
    }

    sum_right <- -Inf
    sum_temp <- 0

    for (i in (mid + 1):right) {
        sum_temp <- sum_temp + arr[i]
        if (sum_temp > sum_right) {
            sum_right <- sum_temp
        }
    }

    return(sum_left + sum_right)
}

max_sub_array_sum_util <- function(arr, left, right) {
    if (left == right) {
        return(arr[left])
    }

    mid <- floor((left + right) / 2)

    return(max(
        max_sub_array_sum_util(arr, left, mid),
        max_sub_array_sum_util(arr, mid + 1, right),
        max_crossing_sum(arr, left, mid, right)
    ))
}

max_sub_array_sum <- function(arr) {
    if (length(arr) == 0) {
        return(0)
    }
    return(max_sub_array_sum_util(arr, 1, length(arr)))
}

