smallest_substring_with_distinct_chars <- function(s, p) {
    if (p == 0) {
        return(0)
    }

    n <- nchar(s)
    if (p > n) {
        return(-1)
    }

    left <- 1  # R uses 1-based indexing
    right <- 1
    char_count <- list()
    distinct_count <- 0
    min_length <- Inf

    while (right <= n) {
        current_char <- substr(s, right, right)
        if (is.null(char_count[[current_char]])) {
            char_count[[current_char]] <- 0
        }
        char_count[[current_char]] <- char_count[[current_char]] + 1
        if (char_count[[current_char]] == 1) {
            distinct_count <- distinct_count + 1
        }

        while (distinct_count >= p) {
            current_window_length <- right - left + 1
            if (current_window_length < min_length) {
                min_length <- current_window_length
            }
            left_char <- substr(s, left, left)
            char_count[[left_char]] <- char_count[[left_char]] - 1
            if (char_count[[left_char]] == 0) {
                distinct_count <- distinct_count - 1
            }
            left <- left + 1
        }

        right <- right + 1
    }

    if (min_length != Inf) {
        return(min_length)
    } else {
        return(-1)
    }
}

