regex_match <- function(pattern, text) {
  match_helper <- function(p_idx, t_idx) {
    if (p_idx > nchar(pattern)) {  # End of pattern
      return(t_idx > nchar(text))  # TRUE if end of text, else FALSE
    }

    first_match <- t_idx <= nchar(text) && 
      (substr(pattern, p_idx, p_idx) == substr(text, t_idx, t_idx) || 
       substr(pattern, p_idx, p_idx) == ".")

    if (p_idx + 1 <= nchar(pattern) && substr(pattern, p_idx + 1, p_idx + 1) == "*") {
      return(match_helper(p_idx + 2, t_idx) || 
             (first_match && match_helper(p_idx, t_idx + 1)))
    } else {
      return(first_match && match_helper(p_idx + 1, t_idx + 1))
    }
  }

  return(match_helper(1, 1))
}

