# max_area calculates the maximum amount of water a container can store, formed by two lines from the input vector.
# The function uses a two-pointer approach to efficiently find the maximum area.
# 
# Args:
#   nums: A numeric vector representing the height of lines.
#
# Returns:
#   The maximum amount of water a container can store.
max_area <- function(nums) {
    left <- 1
    right <- length(nums)
    max_water <- 0

    while (left < right) {
        height <- min(nums[left], nums[right])
        width <- right - left
        current_area <- height * width
        max_water <- max(max_water, current_area)

        if (nums[left] < nums[right]) {
            left <- left + 1
        } else {
            right <- right - 1
        }
    }

    return(max_water)
}

