min_operations_to_make_distinct <- function(S) {
    # Count frequency of each character
    char_count <- list()
    for (char in strsplit(S, "")[[1]]) {
        if (char %in% names(char_count)) {
            char_count[[char]] <- char_count[[char]] + 1
        } else {
            char_count[[char]] <- 1
        }
    }

    # Calculate the number of operations needed
    operations <- 0
    for (count in char_count) {
        if (count > 1) {
            operations <- operations + (count - 1)
        }
    }

    return(operations)
}

process_test_cases <- function(test_cases) {
    results <- list()
    for (S in test_cases) {
        results <- append(results, min_operations_to_make_distinct(S))
    }
    return(results)
}

