min_days_to_complete_projects <- function(arr, k) {
    n <- length(arr)

    # Base cases
    if (n == 0) {
        return(0)
    }
    if (n == 1) {
        return(arr[1])
    }

    # Initialize the count of days
    days <- 0
    i <- 1  # R uses 1-based indexing

    while (i <= n) {
        days <- days + arr[i]
        if (i < n) {
            days <- days + k
        }
        i <- i + 1
    }

    return(days)
}

