can_form_by_deleting_one <- function(s, t) {
    if (nchar(s) != nchar(t) + 1) {
        return(FALSE)
    }

    s_counter <- table(strsplit(s, "")[[1]])
    t_counter <- table(strsplit(t, "")[[1]])

    diff_count <- 0
    for (char in names(s_counter)) {
        s_count <- s_counter[[char]]
        t_count <- ifelse(char %in% names(t_counter), t_counter[[char]], 0)
        if (s_count != t_count) {
