find_common_free_time <- function(schedule) {
  # Flatten the schedule to get all the busy intervals
  all_busy_intervals <- unlist(schedule, recursive = FALSE)

  # Sort the busy intervals by start time
  all_busy_intervals <- all_busy_intervals[order(sapply(all_busy_intervals, function(x) x[1]))]

  # Merge overlapping intervals
  merged_intervals <- list()
  for (interval in all_busy_intervals) {
    if (length(merged_intervals) == 0 || merged_intervals[[length(merged_intervals)]][2] < interval[1]) {
      merged_intervals <- c(merged_intervals, list(interval))
    } else {
      merged_intervals[[length(merged_intervals)]][2] <- max(merged_intervals[[length(merged_intervals)]][2], interval[2])
    }
  }

  # Find the common free time
  common_free_time <- list()
  work_start <- 0
  work_end <- 1440

  for (interval in merged_intervals) {
    if (work_start < interval[1]) {
      common_free_time <- c(common_free_time, list(c(work_start, interval[1])))
    }
    work_start <- interval[2]
  }

  if (work_start < work_end) {
    common_free_time <- c(common_free_time, list(c(work_start, work_end)))
  }

  return(common_free_time)
}

