toggle_bulbs <- function(n, operations) {
  bulbs <- rep(0, n)

  for (op in operations) {
    left <- op[1]
    right <- op[2]
    bulbs[left:right] <- 1 - bulbs[left:right]
  }

  return(bulbs)
}

