# is_quasi_fibonacci determines if a given numeric vector follows a quasi-fibonacci sequence.
# A quasi-fibonacci sequence is defined such that each element (from the third onward) 
# is either equal to one of the two preceding elements (if they are non-zero) 
# or their sum. The function checks this condition for all elements in the sequence.
# 
# Args:
#   sequence: A numeric vector representing the sequence to be checked.
#
# Returns:
#   A logical value (TRUE or FALSE) indicating whether the sequence is quasi-fibonacci.
is_quasi_fibonacci <- function(sequence) {
    if (length(sequence) < 3) {
        return(TRUE)
    }

    for (i in 3:length(sequence)) {
        a <- sequence[i-2]
        b <- sequence[i-1]
        possible_next_values <- c()

        if (a == 0 && b == 0) {
            possible_next_values <- c(possible_next_values, 0)
        } else {
            if (a != 0) {
                possible_next_values <- c(possible_next_values, a)
            }
            if (b != 0) {
                possible_next_values <- c(possible_next_values, b)
            }
            possible_next_values <- c(possible_next_values, a + b)
        }

        if (!(sequence[i] %in% possible_next_values)) {
            return(FALSE)
        }
    }

    return(TRUE)
}

