# has_unique_characters determines if a given string contains all unique characters.
# It assumes the string contains only lowercase alphabetical characters.
# The function uses a logical vector to track seen characters for efficiency.
#
# Parameters:
#   s - Input string (character vector of length 1 in R)
#
# Returns:
#   TRUE if all characters are unique, FALSE otherwise
has_unique_characters <- function(s) {
  # Convert the string to a vector of individual characters
  chars <- strsplit(s, "")[[1]]

  # Initialize a logical vector to track seen characters (for 'a' to 'z')
  char_seen <- logical(26)

  for (char in chars) {
    # Calculate the index for the character (a=1, b=2, ..., z=26)
    index <- utf8ToInt(char) - utf8ToInt('a') + 1

    if (char_seen[index]) {
      return(FALSE)
    }
    char_seen[index] <- TRUE
  }

  return(TRUE)
}

