# balance_bus_capacity calculates the minimum total capacity increase required for bus segments
# to meet passenger loads and returns the new capacities for each segment.
#
# Parameters:
#   n: Number of stops (integer)
#   segments: List of tuples (ci, pi), where ci is the current capacity and pi is the passenger load for each segment
#
# Returns:
#   A list containing the total capacity increase (numeric) and a vector of new capacities (numeric vector)
balance_bus_capacity <- function(n, segments) {
    total_increase <- 0
    new_capacities <- c()

    for (segment in segments) {
        ci <- segment[1]
        pi <- segment[2]

        if (ci < pi) {
            increase <- pi - ci
            total_increase <- total_increase + increase
            new_capacities <- c(new_capacities, pi)
        } else {
            new_capacities <- c(new_capacities, ci)
        }
    }

    return(list(total_increase = total_increase, new_capacities = new_capacities))
}

# longest_subarray_with_sum finds the length of the longest subarray with a given sum.
# It uses a hash map to store the first occurrence of each cumulative sum encountered.
# This allows efficient checking for the presence of (cumulative_sum - target_sum) to determine subarrays that sum to the target.
# 
# Parameters:
# - arr: A numeric vector representing the input array of integers.
# - target_sum: An integer representing the target sum for which the longest subarray's length is to be found.
#
# Returns:
# - An integer representing the length of the longest subarray which adds up to `target_sum`.
longest_subarray_with_sum <- function(arr, target_sum) {
    cumulative_sum <- 0
    max_length <- 0
    sum_to_index <- list()
    sum_to_index[["0"]] <- -1  # Initialize with cumulative sum 0 at index -1

    for (index in seq_along(arr)) {
        value <- arr[index]
        cumulative_sum <- cumulative_sum + value

        # If cumulative sum equals the target sum, update the max length
        if (cumulative_sum == target_sum) {
            max_length <- index
        }

        # If (cumulative_sum - target_sum) exists in sum_to_index, update max length
        key <- as.character(cumulative_sum - target_sum)
        if (key %in% names(sum_to_index)) {
            max_length <- max(max_length, index - sum_to_index[[key]])
        }

        # Store the first occurrence of each cumulative sum
        current_key <- as.character(cumulative_sum)
        if (!(current_key %in% names(sum_to_index))) {
            sum_to_index[[current_key]] <- index
        }
    }

    return(max_length)
}

