# find_special_triplets counts the number of special triplets in a numeric vector where:
# a + b > c, b + c > a, and c + a > b (triangle inequality conditions).
# The function first sorts the array and then uses a two-pointer approach to efficiently count valid triplets.
# Input: arr - a numeric vector
# Output: the count of valid triplets
find_special_triplets <- function(arr) {
  n <- length(arr)
  count <- 0
  arr <- sort(arr)

  for (i in 1:(n - 2)) {
    k <- i + 2
    for (j in (i + 1):(n - 1)) {
      while (k <= n && arr[i] + arr[j] > arr[k]) {
        k <- k + 1
      }
      count <- count + k - j - 1
    }
  }

  return(count)
}

