reverse_string <- function(s, method) {
    if (method == 'recursive') {
        return(reverse_recursive(s))
    } else if (method == 'iterative') {
        return(reverse_iterative(s))
    } else if (method == 'pythonic') {
        return(reverse_pythonic(s))
    } else if (method == 'ultra_pythonic') {
        return(reverse_ultra_pythonic(s))
    } else {
        stop("Invalid method.")
    }
}

reverse_recursive <- function(s) {
    if (nchar(s) == 0) {
        return(s)
    } else {
        return(paste0(substr(s, nchar(s), nchar(s)), reverse_recursive(substr(s, 1, nchar(s) - 1))))
    }
}

reverse_iterative <- function(s) {
    result <- ""
    for (i in 1:nchar(s)) {
        result <- paste0(substr(s, i, i), result)
    }
    return(result)
}

reverse_pythonic <- function(s) {
    chars <- strsplit(s, NULL)[[1]]
    reversed_chars <- rev(chars)
    return(paste0(reversed_chars, collapse = ""))
}

reverse_ultra_pythonic <- function(s) {
    chars <- strsplit(s, NULL)[[1]]
    reversed_chars <- chars[length(chars):1]
    return(paste0(reversed_chars, collapse = ""))
}

