buddy_strings <- function(s, goal) {
  if (nchar(s) != nchar(goal)) {
    return(FALSE)
  }

  if (s == goal) {
    chars <- strsplit(s, "")[[1]]
    return(length(unique(chars)) < length(chars))
  }

  pairs <- list()
  s_chars <- strsplit(s, "")[[1]]
  goal_chars <- strsplit(goal, "")[[1]]

  for (i in 1:length(s_chars)) {
    if (s_chars[i] != goal_chars[i]) {
      pairs <- c(pairs, list(c(s_chars[i], goal_chars[i])))
      if (length(pairs) > 2) {
        return(FALSE)
      }
    }
  }

  return(length(pairs) == 2 && identical(pairs[[1]], rev(pairs[[2]])))
}

