# count_similar_pairs calculates the number of similar pairs of binary strings in a list.
# Two binary strings are considered similar if they differ by exactly one position.
# The function uses nested loops to compare each pair of strings and counts the similar pairs.
count_similar_pairs <- function(binary_strings) {
  # is_similar checks if two binary strings differ by exactly one position.
  # It takes two binary strings as input and returns TRUE if they are similar, FALSE otherwise.
  is_similar <- function(s, t) {
    # Convert strings to vectors of characters for comparison
    s_chars <- strsplit(s, "")[[1]]
    t_chars <- strsplit(t, "")[[1]]
    # Count the number of differing positions
    difference_count <- sum(s_chars != t_chars)
    return(difference_count == 1)
  }

  count <- 0
  n <- length(binary_strings)

  # Compare each pair of strings
  for (i in 1:(n - 1)) {
    for (j in (i + 1):n) {
      if (is_similar(binary_strings[i], binary_strings[j])) {
        count <- count + 1
      }
    }
  }

  return(count)
}

