shortest_nice_subarray <- function(nums, k) {
    n <- length(nums)
    left <- 1  # R uses 1-based indexing
    count <- list()
    distinct_count <- 0
    min_len <- n + 1  # Initialize with a value larger than any possible subarray length

    for (right in 1:n) {
        num <- nums[right]
        if (is.null(count[[as.character(num)]])) {
            count[[as.character(num)]] <- 0
        }
        if (count[[as.character(num)]] == 0) {
            distinct_count <- distinct_count + 1
        }
        count[[as.character(num)]] <- count[[as.character(num)]] + 1

        while (distinct_count == k) {
            current_len <- right - left + 1
            if (current_len < min_len) {
                min_len <- current_len
            }
            left_num <- nums[left]
            count[[as.character(left_num)]] <- count[[as.character(left_num)]] - 1
            if (count[[as.character(left_num)]] == 0) {
                distinct_count <- distinct_count - 1
            }
            left <- left + 1
        }
    }

    if (min_len <= n) {
        return(min_len)
    } else {
        return(-1)
    }
}

