min_partition_sum <- function(arr, n) {
  is_valid_partition <- function(max_sum) {
    current_sum <- 0
    partitions <- 1
    for (num in arr) {
      if (current_sum + num > max_sum) {
        partitions <- partitions + 1
        current_sum <- num
        if (partitions > n) {
          return(FALSE)
        }
      } else {
        current_sum <- current_sum + num
      }
    }
    return(TRUE)
  }

  left <- max(arr)
  right <- sum(arr)
  result <- right

  while (left <= right) {
    mid <- floor((left + right) / 2)
    if (is_valid_partition(mid)) {
      result <- mid
      right <- mid - 1
    } else {
      left <- mid + 1
    }
  }

  return(result)
}

