# has_subarray_with_sum determines if there is a contiguous subarray within `arr` that sums up to `target`.
# It uses a set to keep track of prefix sums encountered so far to efficiently check for the target sum.
# 
# Parameters:
#   arr (numeric vector): The vector of integers.
#   target (numeric): The target sum.
#
# Returns:
#   logical: TRUE if such a subarray exists, FALSE otherwise.
has_subarray_with_sum <- function(arr, target) {
  curr_sum <- 0
  prefix_sums <- new.env(hash = TRUE, size = length(arr))  # Using an environment as a set

  for (num in arr) {
    curr_sum <- curr_sum + num
    if (curr_sum == target || exists(as.character(curr_sum - target), prefix_sums)) {
      return(TRUE)
    }
    prefix_sums[[as.character(curr_sum)]] <- TRUE
  }

  return(FALSE)
}

