# calculate_histogram computes the histogram of pixel intensities for a given grayscale image.
# The function takes a 2D matrix (list of lists) where each element is an integer between 0 and 255.
# It returns a vector of 256 integers representing the count of each pixel intensity.
calculate_histogram <- function(image) {
    histogram <- rep(0, 256)

    for (row in image) {
        for (pixel in row) {
            # Increment the count for the current pixel intensity
            histogram[pixel + 1] <- histogram[pixel + 1] + 1  # R uses 1-based indexing
        }
    }

    return(histogram)
}

# find_hotspot_submatrix identifies if there exists any k x k submatrix in an n x m matrix 
# where the sum of elements is greater than a threshold t. If found, it returns the top-left 
# position of the first such submatrix; otherwise, it returns "NO".
# 
# Parameters:
#   n: Number of rows in the matrix.
#   m: Number of columns in the matrix.
#   t: Threshold value for the sum of elements in the submatrix.
#   readings: A 2D matrix (list of lists) of numeric values.
#
# Returns:
#   A string indicating if a hotspot was found ("YES" followed by the position) or "NO".

find_hotspot_submatrix <- function(n, m, t, readings) {
  # Helper function to calculate the sum of elements in a k x k submatrix starting at (x, y)
  get_submatrix_sum <- function(x, y, k) {
    total <- 0
    for (i in x:(x + k - 1)) {
      for (j in y:(y + k - 1)) {
        total <- total + readings[[i]][[j]]
      }
    }
    return(total)
  }

  max_k <- min(n, m)
  for (k in 1:max_k) {
    for (i in 1:(n - k + 1)) {
      for (j in 1:(m - k + 1)) {
        current_sum <- get_submatrix_sum(i, j, k)
        if (current_sum > t) {
          return(paste("YES", i, j))
        }
      }
    }
  }
  return("NO")
}

