can_divide_equal_coins <- function(nums) {
  total_sum <- sum(nums)
  # If total sum is odd, we can't divide it into two equal parts
  if (total_sum %% 2 != 0) {
    return(FALSE)
  }

  target_sum <- total_sum %/% 2
  n <- length(nums)

  # dp[i] will be TRUE if a subset of nums[1..i] has a sum equal to `target_sum`
  dp <- rep(FALSE, target_sum + 1)
  dp[1] <- TRUE  # Base case: sum of 0 is always possible with an empty subset

  for (coin in nums) {
    for (j in target_sum:coin) {
      if (dp[j - coin + 1]) {
        dp[j + 1] <- TRUE
      }
    }
  }

  return(dp[target_sum + 1])
}

