# Function to check if the knight can reach the target position in exactly K moves
# Parameters:
#   M, N: dimensions of the chessboard (rows, columns)
#   x_start, y_start: starting position of the knight (0-based)
#   x_target, y_target: target position to reach (0-based)
#   K: exact number of moves allowed
# Returns: "YES" if the target can be reached in exactly K moves, "NO" otherwise
can_reach_in_exactly_k_moves <- function(M, N, x_start, y_start, x_target, y_target, K) {
  moves <- list(
    c(2, 1), c(2, -1), c(-2, 1), c(-2, -1),
    c(1, 2), c(1, -2), c(-1, 2), c(-1, -2)
  )

  queue <- list(list(x = x_start, y = y_start, d = 0))
  visited <- list()
  visited[[paste(x_start, y_start, 0, sep = ",")]] <- TRUE

  while (length(queue) > 0) {
    current <- queue[[1]]
    queue <- queue[-1]
    x <- current$x
    y <- current$y
    d <- current$d

    if (d == K) {
      if (x == x_target && y == y_target) {
        return("YES")
      }
      next
    }

    for (move in moves) {
      nx <- x + move[1]
      ny <- y + move[2]
      key <- paste(nx, ny, d + 1, sep = ",")

      if (nx >= 0 && nx < M && ny >= 0 && ny < N && is.null(visited[[key]])) {
        queue <- append(queue, list(list(x = nx, y = ny, d = d + 1)))
        visited[[key]] <- TRUE
      }
    }
  }

  return("NO")
}

