# sum_of_integers calculates the sum of integers in a numeric vector.
# If the input is not a numeric vector or contains non-numeric elements, it returns NULL.
# This function handles potential errors gracefully by catching exceptions.
sum_of_integers <- function(int_list) {
  tryCatch({
    total <- sum(int_list)
    return(total)
  }, error = function(e) {
    return(NULL)
  })
}

