# has_perfect_attendance checks if an employee has perfect attendance based on the given criteria.
# The function returns TRUE if the attendance record contains no 'A' characters or has a single 'A' 
# that appears after some 'P' and no further 'A' after the first one. Otherwise, it returns FALSE.
has_perfect_attendance <- function(attendance) {
  a_found <- FALSE

  for (i in 1:nchar(attendance)) {
    current_char <- substr(attendance, i, i)
    if (current_char == 'A') {
      if (a_found) {
        return(FALSE)  # A second 'A' found
      } else {
        a_found <- TRUE
        if (i == 1) {
          return(FALSE)  # 'A' cannot be at the beginning
        }
      }
    }
  }

  return(TRUE)
}

