# find_min_integer_to_remove returns the smallest integer to be removed to maximize the sum of the remaining integers.
# If there are multiple choices, it returns the smallest integer.
# The function works by iterating through the sorted list of numbers, calculating the sum when each number is removed,
# and keeping track of the number that, when removed, results in the maximum sum.
find_min_integer_to_remove <- function(numbers) {
  total_sum <- sum(numbers)
  max_sum <- -Inf
  candidate <- NULL

  for (num in sort(numbers)) {
    current_sum <- total_sum - num

    if (current_sum > max_sum) {
      max_sum <- current_sum
      candidate <- num
    } else if (current_sum == max_sum) {
      candidate <- min(candidate, num)
    }
  }

  return(candidate)
}

