walls_and_gates <- function(rooms) {
  m <- nrow(rooms)
  n <- ncol(rooms)
  queue <- list()

  # Initialize the queue with all gate positions
  for (i in 1:m) {
    for (j in 1:n) {
      if (rooms[i, j] == 0) {
        queue <- append(queue, list(c(i, j)))
      }
    }
  }

  # Directions for moving up, down, left, right
  directions <- list(c(-1, 0), c(1, 0), c(0, -1), c(0, 1))

  # Process each position in the queue
  while (length(queue) > 0) {
    current <- queue[[1]]
    queue <- queue[-1]
    x <- current[1]
    y <- current[2]

    # Explore all four possible directions
    for (dir in directions) {
      nx <- x + dir[1]
      ny <- y + dir[2]

      # Check if the new position is within bounds and is an empty room
      if (nx >= 1 && nx <= m && ny >= 1 && ny <= n && rooms[nx, ny] == 2147483647) {
        rooms[nx, ny] <- rooms[x, y] + 1
        queue <- append(queue, list(c(nx, ny)))
      }
    }
  }

  return(rooms)
}

