# separate_numbers categorizes the elements of a numeric vector into three groups:
# positive numbers, negative numbers, and zeros.
# It returns a list containing three vectors: positive, negative, and zeros.
separate_numbers <- function(arr) {
  pos <- arr[arr > 0]
  neg <- arr[arr < 0]
  neither <- arr[arr == 0]
  return(list(pos = pos, neg = neg, neither = neither))
}

longest_valid_sequence <- function(n, k, arr) {
  # Initialize deques to store indices of elements in the current window
  min_deque <- integer(0)
  max_deque <- integer(0)
  left <- 1  # R uses 1-based indexing
  max_len <- 0

  for (right in 1:n) {
    # Maintain the min_deque: remove elements from the end that are >= current element
    while (length(min_deque) > 0 && arr[min_deque[length(min_deque)]] >= arr[right]) {
      min_deque <- min_deque[-length(min_deque)]
    }
    min_deque <- c(min_deque, right)

    # Maintain the max_deque: remove elements from the end that are <= current element
    while (length(max_deque) > 0 && arr[max_deque[length(max_deque)]] <= arr[right]) {
      max_deque <- max_deque[-length(max_deque)]
    }
    max_deque <- c(max_deque, right)

    # Validate the current window: if max - min > k, move left pointer
    while (arr[max_deque[1]] - arr[min_deque[1]] > k) {
      left <- left + 1
      if (min_deque[1] < left) {
        min_deque <- min_deque[-1]
      }
      if (max_deque[1] < left) {
        max_deque <- max_deque[-1]
      }
    }

    # Update the maximum length of valid sequence
    max_len <- max(max_len, right - left + 1)
  }

  return(max_len)
}

