# decode_string decodes an encoded string where each digit indicates the number of times 
# the following lowercase letter appears. For example, "3a2b1c" is decoded to "aaabbc".
#
# Parameters:
# - encoded (str): Encoded string, e.g., "3a2b1c".
#
# Returns:
# - str: Decoded string, e.g., "aaabbc".
decode_string <- function(encoded) {
    decoded <- character(0)
    i <- 1
    length <- nchar(encoded)

    while (i <= length) {
        # Get the count
        count <- 0
        while (i <= length && grepl("[0-9]", substr(encoded, i, i))) {
            count <- count * 10 + as.integer(substr(encoded, i, i))
            i <- i + 1
        }

        if (i <= length && grepl("[a-z]", substr(encoded, i, i))) {
            char <- substr(encoded, i, i)
            decoded <- c(decoded, rep(char, count))
            i <- i + 1
        }
    }

    return(paste(decoded, collapse = ""))
}

