# directed_graph.R
# This script implements a directed graph data structure in R with functions to add nodes, add edges,
# and check for the existence of a path between two nodes using depth-first search (DFS).

# The DirectedGraph class is implemented as a list with nodes, edges, and an adjacency list.
# The functions include:
# - add_node: Adds a node to the graph if it doesn't already exist.
# - add_edge: Adds a directed edge between two nodes.
# - has_path: Checks if there is a path from one node to another using DFS.

# Initialize a new DirectedGraph
new_directed_graph <- function(load_dict = list()) {
  graph <- list(
    nodes = c(),
    edges = list(),
    adjacency_list = list()
  )

  # Load the graph from a dictionary if provided
  if (length(load_dict) > 0 && is.list(load_dict)) {
    for (vertex in names(load_dict)) {
      node_from <- add_node(graph, vertex)
      graph$adjacency_list[[node_from]] <- c()
      for (neighbor in load_dict[[vertex]]) {
        node_to <- add_node(graph, neighbor)
        graph$adjacency_list[[node_from]] <- c(graph$adjacency_list[[node_from]], node_to)
        graph <- add_edge(graph, vertex, neighbor)
      }
    }
  }

  return(graph)
}

# Add a node to the graph if it doesn't already exist
add_node <- function(graph, node_name) {
  if (!(node_name %in% graph$nodes)) {
    graph$nodes <- c(graph$nodes, node_name)
  }
  return(node_name)
}

# Add a directed edge from node_name_from to node_name_to
add_edge <- function(graph, node_name_from, node_name_to) {
  if (is.null(graph$adjacency_list[[node_name_from]])) {
    graph$adjacency_list[[node_name_from]] <- c()
  }
  if (!(node_name_to %in% graph$adjacency_list[[node_name_from]])) {
    graph$adjacency_list[[node_name_from]] <- c(graph$adjacency_list[[node_name_from]], node_name_to)
    graph$edges[[length(graph$edges) + 1]] <- c(node_name_from, node_name_to)
  }
  return(graph)
}

# Check if there is a path from start_node to end_node using DFS
has_path <- function(graph, start_node, end_node) {
  visited <- c()

  dfs <- function(node) {
    if (node %in% visited) {
      return(FALSE)
    }
    if (node == end_node) {
      return(TRUE)
    }
    visited <<- c(visited, node)
    for (neighbor in graph$adjacency_list[[node]]) {
      if (dfs(neighbor)) {
        return(TRUE)
      }
    }
    return(FALSE)
  }

  return(dfs(start_node))
}

