# manage_book_loans processes a list of book loan operations and returns the final set of books each member has borrowed.
# The function takes a list of operations, where each operation is a list with 'member', 'action', and 'book_id' elements.
# It returns a named list where each name is a member and the value is a vector of book identifiers they have borrowed.
# Members can borrow up to 3 books at a time. Borrowing beyond this limit is ignored.
# Returning a book removes it from the member's borrowed list.
manage_book_loans <- function(operations) {
  member_books <- list()

  for (operation in operations) {
    member <- operation$member
    action <- operation$action
    book_id <- operation$book_id

    if (!(member %in% names(member_books))) {
      member_books[[member]] <- character(0)
    }

    if (action == 'borrow') {
      if (length(member_books[[member]]) < 3) {
        member_books[[member]] <- c(member_books[[member]], book_id)
      }
    } else if (action == 'return') {
      member_books[[member]] <- setdiff(member_books[[member]], book_id)
    }
  }

  return(member_books)
}

