weather_report <- function(data) {
  if (length(data) == 0) {
    return(list(
      hottest_city = NULL,
      coldest_city = NULL,
      average_temperature = NULL,
      most_humid_city = NULL,
      least_humid_city = NULL
    ))
  }

  # Filter valid entries (must have city, temperature, and humidity)
  valid_entries <- data[sapply(data, function(x) {
    all(c("city", "temperature", "humidity") %in% names(x))
  })]

  if (length(valid_entries) == 0) {
    return(list(
      hottest_city = NULL,
      coldest_city = NULL,
      average_temperature = NULL,
      most_humid_city = NULL,
      least_humid_city = NULL
    ))
  }

  # Extract temperatures and humidities for calculations
  temperatures <- sapply(valid_entries, function(x) x$temperature)
  humidities <- sapply(valid_entries, function(x) x$humidity)
  cities <- sapply(valid_entries, function(x) x$city)

  hottest_city <- cities[which.max(temperatures)]
  coldest_city <- cities[which.min(temperatures)]
  average_temperature <- round(mean(temperatures), 1)
  most_humid_city <- cities[which.max(humidities)]
  least_humid_city <- cities[which.min(humidities)]

  return(list(
    hottest_city = hottest_city,
    coldest_city = coldest_city,
    average_temperature = average_temperature,
    most_humid_city = most_humid_city,
    least_humid_city = least_humid_city
  ))
}

# max_road_distance calculates the maximum consecutive 'R's in each row of a grid.
# It iterates through each row of the grid, counting consecutive 'R's and updating the maximum count found.
# The function returns the maximum count of consecutive 'R's across all rows.
# Parameters:
#   n: Number of rows in the grid (not used in the function, but kept for consistency with the original)
#   m: Number of columns in the grid (not used in the function, but kept for consistency with the original)
#   grid: A list of strings, where each string represents a row in the grid
# Returns:
#   The maximum number of consecutive 'R's in the grid
max_road_distance <- function(n, m, grid) {
  max_distance <- 0
  for (row in grid) {
    current_distance <- 0
    for (cell in strsplit(row, "")[[1]]) {
      if (cell == 'R') {
        current_distance <- current_distance + 1
        max_distance <- max(max_distance, current_distance)
      } else {
        current_distance <- 0
      }
    }
  }
  return(max_distance)
}

