sum_of_smallest_m_integers <- function(n, m, x, arr) {
  filtered_array <- arr[arr != x]
  if (length(filtered_array) < m) {
    return(-1)
  }
  sorted_array <- sort(filtered_array)
  return(sum(sorted_array[1:m]))
}

