flatten_and_count_nested_list <- function(nested_list) {
  # Initialize an empty list to store flattened elements
  flattened <- list()

  # Define a helper function to recursively flatten the list
  flatten <- function(lst) {
    for (item in lst) {
      if (is.list(item)) {
        flatten(item)
      } else {
        flattened <<- c(flattened, item)
      }
    }
  }

  # Flatten the input list
  flatten(nested_list)

  # Convert the list to a vector and filter out non-integer elements
  flattened_vector <- unlist(flattened)
  flattened_vector <- flattened_vector[sapply(flattened_vector, function(x) is.integer(x) || is.numeric(x))]

  # Count occurrences of each integer
  if (length(flattened_vector) == 0) {
    return(NULL)
  }

  count_table <- table(flattened_vector)
  result <- as.vector(count_table)
  names(result) <- names(count_table)

  return(result)
}

