shortest_path_with_obstacles <- function(matrix) {
  # is_valid checks if the given coordinates (x, y) are within the bounds of the matrix.
  is_valid <- function(x, y, n, m) {
    return(x >= 0 && x < n && y >= 0 && y < m)
  }

  n <- nrow(matrix)
  m <- ncol(matrix)

  # Initialize the queue with the starting position (0, 0), distance 0, and no obstacles broken.
  queue <- list(list(row = 0, col = 0, dist = 0, broken = FALSE))

  # visited keeps track of visited positions and whether an obstacle has been broken.
  visited <- list()
  visited[[paste(0, 0, FALSE)]] <- TRUE

  # Directions for moving up, down, left, and right.
  directions <- list(c(-1, 0), c(1, 0), c(0, -1), c(0, 1))

  while (length(queue) > 0) {
    # Dequeue the first element.
    current <- queue[[1]]
    queue <- queue[-1]

    x <- current$row
    y <- current$col
    dist <- current$dist
    broken <- current$broken

    # Check if we've reached the destination.
    if (x == n - 1 && y == m - 1) {
      return(dist)
    }

    # Explore all four directions.
    for (dir in directions) {
      nx <- x + dir[1]
      ny <- y + dir[2]

      if (is_valid(nx, ny, n, m)) {
        # If the next cell is a free space and we haven't visited it with the current broken state.
        if (matrix[nx + 1, ny + 1] == 0 && is.null(visited[[paste(nx, ny, broken)]])) {
          visited[[paste(nx, ny, broken)]] <- TRUE
          queue[[length(queue) + 1]] <- list(row = nx, col = ny, dist = dist + 1, broken = broken)
        }
        # If the next cell is an obstacle, and we haven't broken any obstacle yet.
        if (matrix[nx + 1, ny + 1] == 1 && !broken && is.null(visited[[paste(nx, ny, TRUE)]])) {
          visited[[paste(nx, ny, TRUE)]] <- TRUE
          queue[[length(queue) + 1]] <- list(row = nx, col = ny, dist = dist + 1, broken = TRUE)
        }
      }
    }
  }

  # If no path was found.
  return(-1)
}

