# two_sum_binary_search finds two numbers in a sorted vector that add up to a target value using binary search.
# It iterates through each number and uses binary search to find its complement.
# Returns the 1-based indices of the two numbers if found, otherwise an empty vector.
two_sum_binary_search <- function(numbers, target) {
  binary_search <- function(low, high, value) {
    while (low <= high) {
      mid <- floor((low + high) / 2)
      if (numbers[mid] == value) {
        return(mid)
      } else if (numbers[mid] < value) {
        low <- mid + 1
      } else {
        high <- mid - 1
      }
    }
    return(-1)
  }

  for (i in 1:length(numbers)) {
    complement <- target - numbers[i]
    j <- binary_search(i + 1, length(numbers), complement)
    if (j != -1) {
      return(c(i, j))
    }
  }
  return(c())
}

# two_sum_hash_table finds two numbers in a sorted vector that add up to a target value using a hash table.
# It stores each number's index in a hash table and checks for the complement of each number.
# Returns the 1-based indices of the two numbers if found, otherwise an empty vector.
two_sum_hash_table <- function(numbers, target) {
  num_to_index <- list()
  for (i in 1:length(numbers)) {
    num <- numbers[i]
    complement <- target - num
    if (!is.null(num_to_index[[as.character(complement)]])) {
      return(c(num_to_index[[as.character(complement)]], i))
    }
    num_to_index[[as.character(num)]] <- i
  }
  return(c())
}

# two_sum_two_pointers finds two numbers in a sorted vector that add up to a target value using two pointers.
# It uses one pointer at the start and another at the end, moving them based on the sum comparison with the target.
# Returns the 1-based indices of the two numbers if found, otherwise an empty vector.
two_sum_two_pointers <- function(numbers, target) {
  left <- 1
  right <- length(numbers)
  while (left < right) {
    current_sum <- numbers[left] + numbers[right]
    if (current_sum == target) {
      return(c(left, right))
    } else if (current_sum < target) {
      left <- left + 1
    } else {
      right <- right - 1
    }
  }
  return(c())
}

