longest_valid_word <- function(s) {
    # Use regular expression to find all sequences of Latin letters
    words <- regmatches(s, gregexpr("[A-Za-z]+", s, perl = TRUE))[[1]]
    if (length(words) == 0) {
        return("")
    }
    # Find the longest word
    longest_word <- words[which.max(nchar(words))]
    return(longest_word)
}

