# minimum_card_pair_subarray_length determines the minimum length of a subarray that contains at least one card pair.
# It returns -1 if no such subarray exists.
# 
# Parameters:
#   cards: A numeric vector representing card numbers.
#
# Returns:
#   The minimum length of a subarray containing at least one card pair, or -1 if not possible.
minimum_card_pair_subarray_length <- function(cards) {
    card_indices <- list()
    min_length <- Inf

    for (i in seq_along(cards)) {
        card <- cards[i]
        if (as.character(card) %in% names(card_indices)) {
            current_length <- i - card_indices[[as.character(card)]] + 1
            min_length <- min(min_length, current_length)
        }
        card_indices[[as.character(card)]] <- i
    }

    if (is.infinite(min_length)) {
        return(-1)
    } else {
        return(min_length)
    }
}

