has_path_in_maze <- function(maze, start, destination) {
  rows <- length(maze)
  if (rows == 0) return(FALSE)
  cols <- length(maze[[1]])

  is_valid <- function(x, y) {
    x >= 1 && x <= rows && y >= 1 && y <= cols && maze[[x]][y] == "."
  }

  bfs <- function(start) {
    queue <- list()
    queue[[1]] <- start
    visited <- matrix(FALSE, nrow = rows, ncol = cols)
    visited[start[1], start[2]] <- TRUE

    while (length(queue) > 0) {
      current <- queue[[1]]
      queue <- queue[-1]
      x <- current[1]
      y <- current[2]

      if (x == destination[1] && y == destination[2]) {
        return(TRUE)
      }

      directions <- list(c(-1, 0), c(1, 0), c(0, -1), c(0, 1))

      for (dir in directions) {
        dx <- dir[1]
        dy <- dir[2]
        new_x <- x
        new_y <- y

        while (is_valid(new_x + dx, new_y + dy)) {
          new_x <- new_x + dx
          new_y <- new_y + dy
        }

        if (!visited[new_x, new_y]) {
          visited[new_x, new_y] <- TRUE
          queue[[length(queue) + 1]] <- c(new_x, new_y)
        }
      }
    }

    return(FALSE)
  }

  return(bfs(start))
}

