# count_ways_to_tile calculates the number of ways to tile a 2 x n board using 2 x 1 tiles.
# It uses dynamic programming to solve the problem, where dp[i] represents the number of ways to tile a 2 x i board.
# The recurrence relation is dp[i] = dp[i-1] + dp[i-2], similar to the Fibonacci sequence.
# The result is returned modulo 10^9 + 7 to handle large numbers.
count_ways_to_tile <- function(n) {
  MOD <- 10^9 + 7
  if (n == 0) {
    return(1)
  }
  if (n == 1) {
    return(1)
  }
  dp <- integer(n + 1)
  dp[1] <- 1  # dp[0] is 1, but R uses 1-based indexing, so dp[1] corresponds to n=0
  dp[2] <- 1  # dp[2] corresponds to n=1
  for (i in 3:(n + 1)) {
    dp[i] <- (dp[i - 1] + dp[i - 2]) %% MOD
  }
  return(dp[n + 1])
}

