# is_all_nested_lists checks whether all elements in the provided list are lists, including nested ones.
# 
# Parameters:
#   lst (list): The list to check.
#
# Returns:
#   logical: TRUE if all elements are lists, including nested ones, FALSE otherwise.
is_all_nested_lists <- function(lst) {
  if (!is.list(lst)) {
    return(FALSE)
  }
  for (element in lst) {
    if (!is.list(element)) {
      return(FALSE)
    }
    if (!is_all_nested_lists(element)) {
      return(FALSE)
    }
  }
  return(TRUE)
}

