#   lst (numeric vector): Vector of integers.
#
# Returns:
#   character vector: Vector of ASCII character representations for given integers in the input vector.
numbers_to_ascii <- function(lst) {
  sapply(lst, function(num) {
    if (0 <= num && num < 256) {
      intToUtf8(num)
    } else {
      '?'
    }
  })
}
test_numbers_to_ascii <- function() {
  stopifnot(identical(numbers_to_ascii(c(65, 66, 67)), c("A", "B", "C")))
  stopifnot(identical(numbers_to_ascii(c(97, 98, 99)), c("a", "b", "c")))
  stopifnot(identical(numbers_to_ascii(c(0, 127, 255)), c("\x00", "\x7f", "ÿ")))
  stopifnot(identical(numbers_to_ascii(c(-1, 256, 1000)), c("?", "?", "?")))
  stopifnot(identical(numbers_to_ascii(c(72, 101, 108, 108, 111)), c("H", "e", "l", "l", "o")))
}

test_numbers_to_ascii()

