# count_islands calculates the number of "islands" in a numeric vector.
# An "island" is defined as a contiguous sequence of increasing numbers.
# The function iterates through the vector and counts each increasing sequence as a single island.
# If the vector has fewer than 2 elements, it returns 0 since no islands can exist.
count_islands <- function(arr) {
  if (length(arr) < 2) {
    return(0)
  }

  count <- 0
  i <- 1  # R uses 1-based indexing

  while (i < length(arr)) {
    if (arr[i] < arr[i + 1]) {
      count <- count + 1
      while (i < length(arr) && arr[i] <= arr[i + 1]) {
        i <- i + 1
      }
    }
    i <- i + 1
  }

  return(count)
}

