# efficient_remove_duplicates returns a new vector with duplicates removed, 
# keeping only the first occurrence of each element and maintaining the initial order.
# It uses a hash set to track seen elements for efficient lookup.
efficient_remove_duplicates <- function(array) {
  seen <- new.env(hash = TRUE, size = length(array))
  result <- vector()
  for (item in array) {
    if (!exists(as.character(item), envir = seen)) {
      seen[[as.character(item)]] <- TRUE
      result <- c(result, item)
    }
  }
  return(result)
}

