# find_max_and_min returns a list with the maximum and minimum values from a given vector of integers.
# If the vector is empty, it returns a list with NULL for both max and min values.
#
# Args:
#   int_vector: A vector of integers
#
# Returns:
#   A list containing the maximum and minimum values (max_value, min_value)
find_max_and_min <- function(int_vector) {
  if (length(int_vector) == 0) {
    return(list(max_value = NULL, min_value = NULL))
  }

  max_value <- max(int_vector)
  min_value <- min(int_vector)

  return(list(max_value = max_value, min_value = min_value))
}

# max_profit calculates the maximum profit by buying and selling stock exactly once.
# It iterates through the list of prices, keeping track of the minimum price seen so far
# and calculating the potential profit if sold at the current price.
# If no profit can be made, it returns 0.
max_profit <- function(prices) {
  if (length(prices) == 0) {
    return(0)
  }

  min_price <- prices[1]
  max_profit <- 0

  for (price in prices) {
    # Calculate the profit if we sold at current price
    profit <- price - min_price
    # Update the max profit
    max_profit <- max(max_profit, profit)
    # Update the min price to be the lowest price seen so far
    min_price <- min(min_price, price)
  }

  return(max_profit)
}

