find_number_of_roads_to_cut <- function(n, m, roads) {
    if (m == 0) {
        return(0)
    }

    parent <- 1:(n + 1)  # 1-based indexing for easier handling in R

    find <- function(x) {
        if (parent[x] != x) {
            parent[x] <<- find(parent[x])  # Use <<- for assignment in parent scope
        }
        return(parent[x])
    }

    union <- function(x, y) {
        rootX <- find(x)
        rootY <- find(y)
        if (rootX != rootY) {
            parent[rootX] <<- rootY  # Use <<- for assignment in parent scope
            return(TRUE)
        }
        return(FALSE)
    }

    redundant_connections <- 0

    for (road in roads) {
        u <- road[1]
        v <- road[2]
        if (!union(u, v)) {
            redundant_connections <- redundant_connections + 1
        }
    }

    return(redundant_connections)
}

