use std::collections::HashMap;

/// Finds the number of common elements in all rows of a matrix.
/// 
/// # Arguments
/// * `mat` - A 2D vector representing the matrix
/// 
/// # Returns
/// The count of elements that appear in every row of the matrix
fn find_common_elements(mat: &[Vec<i32>]) -> usize {
    if mat.is_empty() {
        return 0;
    }

    // Initialize a HashMap with the elements of the first row
    let mut counter: HashMap<i32, usize> = HashMap::new();
    for &num in &mat[0] {
        *counter.entry(num).or_insert(0) += 1;
    }

    // Intersect the counts with subsequent rows
    for row in mat.iter().skip(1) {
        let mut row_counter: HashMap<i32, usize> = HashMap::new();
        for &num in row {
            *row_counter.entry(num).or_insert(0) += 1;
        }

        // Update the main counter with minimum counts
        for (key, count) in counter.iter_mut() {
            *count = (*count).min(*row_counter.get(key).unwrap_or(&0));
        }
    }

    // Count elements that appear in all rows (count > 0)
    counter.values().filter(|&&count| count > 0).count()
}

assertions:
#[test]
fn test_find_common_elements() {
    assert_eq!(
        find_common_elements(&vec![
            vec![1, 2, 3, 4],
            vec![2, 4, 5, 6],
            vec![3, 4, 7, 8],
        ]),
        1
    );
    assert_eq!(
        find_common_elements(&vec![
            vec![1, 2, 3],
            vec![4, 5, 6],
            vec![7, 8, 9],
        ]),
        0
    );
    assert_eq!(
        find_common_elements(&vec![
            vec![5, 5, 5],
            vec![5, 5, 5],
            vec![5, 5, 5],
        ]),
        1
    );
    assert_eq!(
        find_common_elements(&vec![
            vec![1, 2],
            vec![1, 2],
            vec![2, 3],
        ]),
        1
    );
    assert_eq!(
        find_common_elements(&vec![]),
        0
    );
}

