/// Calculates the minimum number of operations needed to transform string s1 into string s2.
/// Each operation increments a character in s1 by 1 (with 'z' wrapping around to 'a').
/// The function compares characters at each position and sums the differences.
fn min_operations_to_transform(s1: &str, s2: &str) -> u32 {
    // Check that both strings have the same length
    assert_eq!(s1.len(), s2.len(), "Strings must be of equal length");

    let mut operations_needed = 0;

    // Iterate through each character pair
    for (c1, c2) in s1.chars().zip(s2.chars()) {
        if c1 == c2 {
            continue;
        }

        // Calculate the difference between characters, wrapping around using modulo 26
        let diff = (c2 as u32).wrapping_sub(c1 as u32) % 26;
        operations_needed += diff;
    }

    operations_needed
}

assertions:
#[test]
fn test_min_operations_to_transform() {
    assert_eq!(min_operations_to_transform("abc", "abc"), 0);
    assert_eq!(min_operations_to_transform("abc", "abd"), 1);
    assert_eq!(min_operations_to_transform("abc", "abz"), 25);
    assert_eq!(min_operations_to_transform("abz", "abx"), 24);
    assert_eq!(min_operations_to_transform("aaa", "bbb"), 3);
    assert_eq!(min_operations_to_transform("xyz", "abc"), 3);
}

