fn generate_rna_sequences(sequence: &str) -> Vec<String> {
    // Generates all possible valid RNA sequences by replacing each 'N' in the input sequence
    // with all possible nucleotides ('A', 'C', 'G', 'U').

    let nucleotides = ['A', 'C', 'G', 'U'];
    let mut result = vec![String::new()];  // Start with one empty string

    for char in sequence.chars() {
        if char == 'N' {
            // For 'N', expand each existing sequence with all possible nucleotides
            result = result.into_iter().flat_map(|seq| {
                nucleotides.iter().map(move |&nuc| {
                    let mut new_seq = seq.clone();
                    new_seq.push(nuc);
                    new_seq
                })
            }).collect();
        } else {
            // For regular characters, just append them to all existing sequences
            for seq in &mut result {
                seq.push(char);
            }
        }
    }

    result
}

assertions:
#[test]
fn test_generate_rna_sequences() {
    // Test empty input
    assert_eq!(generate_rna_sequences(""), vec![""]);

    // Test single non-N character
    assert_eq!(generate_rna_sequences("A"), vec!["A"]);

    // Test single N character
    assert_eq!(
        generate_rna_sequences("N"),
        vec!["A", "C", "G", "U"]
    );

    // Test AN pattern
    assert_eq!(
        generate_rna_sequences("AN"),
        vec!["AA", "AC", "AG", "AU"]
    );

    // Test NA pattern
    assert_eq!(
        generate_rna_sequences("NA"),
        vec!["AA", "CA", "GA", "UA"]
    );

    // Test ANN pattern
    assert_eq!(
        generate_rna_sequences("ANN"),
        vec![
            "AAA", "AAC", "AAG", "AAU",
            "ACA", "ACC", "ACG", "ACU",
            "AGA", "AGC", "AGG", "AGU",
            "AUA", "AUC", "AUG", "AUU"
        ]
    );

    // Test NNN pattern (all combinations)
    assert_eq!(
        generate_rna_sequences("NNN").len(),
        64
    );
}

