fn count_equal_subarrays(arr: &[i32]) -> i32 {
    // Returns the number of contiguous subarrays where all elements are equal.
    // 
    // Parameters:
    // - arr: A slice of integers
    //
    // Returns:
    // - The count of contiguous subarrays with all equal elements
    let n = arr.len();
    if n == 0 {
        return 0;
    }

    let mut count = 0;
    let mut subarray_length = 1;

    // Iterate over the array starting from the second element
    for i in 1..n {
        if arr[i] == arr[i - 1] {
            subarray_length += 1;
        } else {
            count += (subarray_length * (subarray_length + 1)) / 2;
            subarray_length = 1;
        }
    }

    // Add the last segment
    count += (subarray_length * (subarray_length + 1)) / 2;
    count
}

